mapper.select:
 		select count(*)
 		  from colleague
 		 where mymem_no = #{myMemNo}  
		   and comem_no = #{coMemNo}

mapper.insert:
		insert 
	 	  into colleague
	 	  (
	 	  	mymem_no
	 	  , comem_no	
	 	  )
	 	  values
	 	  (
	 	    #{ myMemNo } 
  	 	  , #{ coMemNo } 
	 	  )

mapper.select:
select room_name
  from chatroom
 where room_no = #{roomNo}

mapper.insert:
	 	insert 
		  into chatlog
		  (
		    chatlog_no
		  , mem_no
		  , room_no
		  , chat_content
		  )
		  values
		  (
		    seq_chno.nextval
		  , #{ memNo }  
		  , #{ roomNo }  
		  , #{ chatContent }  
		  )

        	select seq_chno.currval FROM dual

mapper.update:
update
       chat_connect
   set chatlog_no = #{chatlogNo} ,
       unread_count = 0
 where room_no = #{roomNo} 
   and mem_no = #{memNo}

 		update 
		       chat_connect
		   set unread_count = (select count(*)
	  							 from chatlog
	 							where chatlog_no > (select chatlog_no
						                     		  from chat_connect
						                     		 where room_no = #{ roomNo } 
						                       		   and mem_no = #{ memNo} )
								  and room_no = #{ roomNo }
								  and mem_no != #{ memNo})
		 where room_no = #{roomNo} 
		   and mem_no = #{memNo}

mapper.select:
	 select room_no
       from chat_connect
      where mem_no = #{memNo}

update.foreach:
     	update 
		       chat_connect
		   set unread_count = (select count(*)
	  							 from chatlog
	 							where chatlog_no > (select chatlog_no
						                     		  from chat_connect
						                     		 where room_no = ${ roomNo } 
						                       		   and mem_no = ${ memNo} )
								  and room_no = ${ roomNo }
								  and mem_no != ${ memNo})
		 where room_no = ${roomNo} 
		   and mem_no = #{memNo}
